import React, { useState } from "react";
import GNB from "../components/GNB";
import "./HomePage.css";
import noGroupsImage from "../assets/icon=empty.svg"; // 이미지 경로를 맞게 설정하세요

const HomePage = () => {
  const [isPublic, setIsPublic] = useState(true); // 초기 상태를 공개로 설정

  const handleToggle = () => {
    setIsPublic(!isPublic); // 상태를 토글 (공개 <-> 비공개)
  };

  return (
    <div className="home-page">
      <GNB />
      <div className="group-section">
        <div className="group-toggle">
          <button
            className={`toggle-button ${isPublic ? "active" : ""}`}
            onClick={() => setIsPublic(true)}
          >
            공개
          </button>
          <button
            className={`toggle-button ${!isPublic ? "active" : ""}`}
            onClick={() => setIsPublic(false)}
          >
            비공개
          </button>
          <input
            type="text"
            placeholder="그룹명을 검색해 주세요"
            className="group-search"
          />
        </div>
        <div className="group-content">
          <div className="no-group">
            <img src={noGroupsImage} alt="No Groups" />
            <p>등록된 공개 그룹이 없습니다.</p>
            <p>가장 먼저 그룹을 만들어보세요!</p>
          </div>
          <button className="create-group-button">그룹 만들기</button>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
