import React, { useState } from 'react';
import './GroupForm.css';
import Modal from './Modal';  // Modal 컴포넌트 임포트

function GroupForm() {
  const [groupName, setGroupName] = useState('');
  const [groupNameError, setGroupNameError] = useState('');  // 그룹명 오류 메시지 상태 추가
  const [groupImage, setGroupImage] = useState(null);
  const [groupDescription, setGroupDescription] = useState('');
  const [isPublic, setIsPublic] = useState(true);
  const [password, setPassword] = useState('');
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState({ title: '', message: '' });

  const validateGroupName = (name) => {
    const specialCharPattern = /^[a-zA-Z0-9!@#$%^_]*$/;  // 허용된 특수문자 패턴
    if (!specialCharPattern.test(name)) {
      setGroupNameError('특수문자는 !@#$%^_만 사용하실 수 있습니다.');
      return false;  // 오류가 있을 경우 false 반환
    } else {
      setGroupNameError('');
      return true;  // 오류가 없을 경우 true 반환
    }
  };

  const handleGroupNameChange = (e) => {
    const name = e.target.value;
    setGroupName(name);
    validateGroupName(name);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 제출 시에도 그룹명을 다시 확인
    const isGroupNameValid = validateGroupName(groupName);

    if (!isGroupNameValid) {
      // 그룹명에 오류가 있으면 폼 제출을 중단하고, 에러 상태 유지
      return;
    }

    const requestBody = {
      name: groupName,
      password: password,
      imageUrl: groupImage ? URL.createObjectURL(groupImage) : '',
      isPublic: isPublic,
      introduction: groupDescription
    };

    try {
      const response = await fetch('/api/groups', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (response.status === 201) {
        setModalContent({
          title: '그룹 만들기 성공',
          message: '그룹이 성공적으로 등록되었습니다.',
        });
      } else if (response.status === 400) {
        setModalContent({
          title: '그룹 만들기 실패',
          message: '그룹 등록에 실패했습니다.',
        });
      } else {
        setModalContent({
          title: '그룹 만들기 실패',
          message: '알 수 없는 오류가 발생했습니다.',
        });
      }
      setModalVisible(true);
    } catch (error) {
      setModalContent({
        title: '서버 오류',
        message: '서버와의 통신 중 오류가 발생했습니다.',
      });
      setModalVisible(true);
    }
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="group-form">
        <h2 className="form-title">그룹 만들기</h2>
        
        <label className="form-label">그룹명</label>
        <input 
          type="text" 
          value={groupName} 
          onChange={handleGroupNameChange} 
          placeholder="그룹명을 입력하세요" 
          className={`form-input ${groupNameError ? 'input-error' : ''}`} 
        />
        {groupNameError && <p className="error-message">{groupNameError}</p>}
        
        <label className="form-label">대표 이미지</label>
        <div className="form-input-file">
          <input 
            type="text" 
            placeholder="파일을 선택해 주세요" 
            className="file-input-text" 
            readOnly
          />
          <label className="file-button">
            파일 선택
            <input 
              type="file" 
              onChange={(e) => setGroupImage(e.target.files[0])} 
              style={{ display: 'none' }}
            />
          </label>
        </div>
        
        <label className="form-label">그룹 소개</label>
        <textarea 
          value={groupDescription} 
          onChange={(e) => setGroupDescription(e.target.value)} 
          placeholder="그룹을 소개해 주세요" 
          className="form-textarea" 
        />
        
        <label className="form-label">그룹 공개 선택</label>
        <div className="form-public-toggle">
          <label className="toggle-label">공개</label>
          <label className="switch">
            <input 
              type="checkbox" 
              checked={isPublic} 
              onChange={(e) => setIsPublic(e.target.checked)} 
            />
            <span className="slider round"></span>
          </label>
        </div>

        <label className="form-label">비밀번호 생성</label>
        <input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          placeholder="그룹 비밀번호를 생성해 주세요" 
          className="form-input" 
        />
        
        <button type="submit" className="form-button">만들기</button>
      </form>

      {modalVisible && (
        <Modal 
          title={modalContent.title} 
          message={modalContent.message} 
          onClose={closeModal} 
        />
      )}
    </div>
  );
}

export default GroupForm;
