import React, { useState } from "react";
import "./MemoryDetail.css";
import fishingImage from "../assets/image=img4.png"; // ÎÇöÏãú Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°ú
import flowerIcon from "../assets/size=16.png";
import editIcon from "../assets/icon=edit.png";
import deleteIcon from "../assets/icon=delete.png";
import MemoryEditModal from "./MemoryEditModal"; // Ï∂îÏñµ ÏàòÏ†ï Î™®Îã¨ Ïª¥Ìè¨ÎÑåÌä∏ Í∞ÄÏ†∏Ïò§Í∏∞
import MemoryDeleteModal from "./MemoryDeleteModal"; // Ï∂îÏñµ ÏÇ≠Ï†ú Î™®Îã¨ Ïª¥Ìè¨ÎÑåÌä∏ Í∞ÄÏ†∏Ïò§Í∏∞

const MemoryDetail = () => {
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false); // ÏÇ≠Ï†ú Î™®Îã¨ ÏÉÅÌÉú Ï∂îÍ∞Ä

  const handleEditClick = () => {
    setIsEditModalOpen(true);
  };

  const handleCloseEditModal = () => {
    setIsEditModalOpen(false);
  };

  const handleDeleteClick = () => {
    setIsDeleteModalOpen(true);
  };

  const handleCloseDeleteModal = () => {
    setIsDeleteModalOpen(false);
  };

  return (
    <div className="memory-detail-page">
      <MemoryHeader 
        onEditClick={handleEditClick}
        onDeleteClick={handleDeleteClick} // ÏÇ≠Ï†ú ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨ Ï∂îÍ∞Ä
      />
      <MemoryContent />
      <CommentSection />
      {isEditModalOpen && <MemoryEditModal onClose={handleCloseEditModal} />}
      {isDeleteModalOpen && <MemoryDeleteModal isOpen={isDeleteModalOpen} onClose={handleCloseDeleteModal} />} {/* ÏÇ≠Ï†ú Î™®Îã¨ Ï°∞Í±¥Î∂Ä Î†åÎçîÎßÅ */}
    </div>
  );
};

const MemoryHeader = ({ onEditClick, onDeleteClick }) => { // ÏÇ≠Ï†ú ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨Î•º propsÎ°ú Î∞õÏùå
  return (
    <div className="memory-header-section">
      <div className="memory-meta">
        <div>
          <span className="memory-category">Îã¨Î¥âÏù¥ÏïÑÎì§</span>
          <span className="memory-divider"> | </span>
          <span className="memory-visibility">Í≥µÍ∞ú</span>
        </div>
        <div className="memory-actions">
          <button className="edit-memory" onClick={onEditClick}>
            Ï∂îÏñµ ÏàòÏ†ïÌïòÍ∏∞
          </button>
          <button className="delete-memory" onClick={onDeleteClick}> {/* ÏÇ≠Ï†ú Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Î™®Îã¨ Ïó¥Í∏∞ */}
            Ï∂îÏñµ ÏÇ≠Ï†úÌïòÍ∏∞
          </button>
        </div>
      </div>
      <h1 className="memory-title">Ïù∏Ï≤ú ÏïûÎ∞îÎã§ÏóêÏÑú Î¨¥Î†§ 60cm ÏõîÏ≤ôÏùÑ ÎÇöÎã§!</h1>
      <div className="memory-tags">
        <span className="memory-tag">#Ïù∏Ï≤ú</span>
        <span className="memory-tag">#ÎÇöÏãú</span>
      </div>
      <div className="memory-header-bottom">
        <div>
          <span className="memory-date">Ïù∏Ï≤ú ÏïûÎ∞îÎã§ ¬∑ 24.01.19 18:00</span>
          <span className="memory-likes">
            <img src={flowerIcon} alt="ÍΩÉ ÏïÑÏù¥ÏΩò" className="flower-icon" /> 120
          </span>
          <span className="memory-comments"> üí¨ 8</span>
        </div>
        <div className="send-like-container">
          <button className="send-like">
            <img src={flowerIcon} alt="ÍΩÉ ÏïÑÏù¥ÏΩò" className="flower-icon" />{" "}
            Í≥µÍ∞ê Î≥¥ÎÇ¥Í∏∞
          </button>
        </div>
      </div>
    </div>
  );
};

const MemoryContent = () => {
  return (
    <div className="memory-content">
      <img
        src={fishingImage} // ÎÇöÏãú Ïù¥ÎØ∏ÏßÄ ÏÇ¨Ïö©
        alt="ÎÇöÏãúÌïòÎäî Î™®Ïäµ"
        className="memory-image"
      />
      <p className="memory-text">
        Ïù∏Ï≤ú ÏïûÎ∞îÎã§ÏóêÏÑú ÏõîÏ≤ôÏùÑ ÎÇöÏïòÏäµÎãàÎã§!
        <br />
        Í∞ÄÏ°±Îì§Í≥º Í∏∞ÏñµÏóê Ïò§ÎûòÎèÑÎ°ù ÎÇ®ÏùÑ Î©ãÏßÑ ÌïòÎ£®ÏòÄÏñ¥Ïöî.
      </p>
      <CommentForm /> {/* ÎåìÍ∏Ä Îì±Î°ùÌïòÍ∏∞ Î≤ÑÌäºÏùÑ Î®ºÏ†Ä Î†åÎçîÎßÅ */}
    </div>
  );
};

const CommentForm = () => {
  return (
    <div className="comment-form">
      <button className="comment-submit-button">ÎåìÍ∏Ä Îì±Î°ùÌïòÍ∏∞</button>
    </div>
  );
};

const CommentSection = () => {
  return (
    <div className="comment-section">
      <h2 className="comment-title">ÎåìÍ∏Ä 8</h2> {/* Í∑∏ Îã§ÏùåÏóê ÎåìÍ∏Ä 8 Ï†úÎ™© */}
      <CommentList /> {/* ÎßàÏßÄÎßâÏúºÎ°ú ÎåìÍ∏Ä Î™©Î°ù */}
    </div>
  );
};

const CommentList = () => {
  const comments = [
    {
      id: 1,
      name: "Îã¨Î¥âÏù¥ÏïÑÎπ†",
      date: "24.01.18 21:50",
      text: "Ïö∞ÏôÄ 60cm ÏõîÏ≤ô! ÎÇòÎèÑ Í∞ÄÎ≥¥Í≥† Ïã∂Ïñ¥Ïöî",
    },
    {
      id: 2,
      name: "Ï∞∏Í∞ÄÏûê",
      date: "24.01.18 21:50",
      text: "ÍµâÏû•Ìûà Ï¶êÍ±∞Ïõ†Í≤†Ïñ¥Ïöî!",
    },
    {
      id: 3,
      name: "Ï∞∏Í≤¨Îü¨",
      date: "24.01.18 21:50",
      text: "Îã§ÏùåÏóêÎäî Ï†ÄÎèÑ Ï¥àÎåÄÌï¥ Ï£ºÏÑ∏Ïöî!",
    },
  ];

  return (
    <div className="comment-list">
      {comments.map((comment) => (
        <div className="comment-item" key={comment.id}>
          <div>
            <div className="comment-meta">
              <span className="comment-author">{comment.name}</span>
              <span className="comment-date">{comment.date}</span>
            </div>
            <p className="comment-text">{comment.text}</p>
          </div>
          <div className="comment-actions">
            <img
              src={editIcon}
              alt="ÏàòÏ†ï ÏïÑÏù¥ÏΩò"
              className="comment-edit-icon"
            />
            <img
              src={deleteIcon}
              alt="ÏÇ≠Ï†ú ÏïÑÏù¥ÏΩò"
              className="comment-delete-icon"
            />
          </div>
        </div>
      ))}
    </div>
  );
};

export default MemoryDetail;
