import React, { useState } from "react";
import "./MemoryForm.css";

const MemoryForm = () => {
  const [isPublic, setIsPublic] = useState(true);
  const [selectedFile, setSelectedFile] = useState("");

  const handleToggle = () => {
    setIsPublic(!isPublic);
  };

  const handleFileSelect = (event) => {
    const fileInput = event.target;
    const fileName = fileInput.files[0] ? fileInput.files[0].name : '';
    setSelectedFile(fileName);
  };

  return (
    <div className="memory-form-page">
      <div className="memory-form-container">
        <h1>추억 올리기</h1>
        <div className="memory-form">
          <div className="form-left">
            <label>닉네임</label>
            <input type="text" placeholder="닉네임을 입력해 주세요" />

            <label>제목</label>
            <input type="text" placeholder="제목을 입력해 주세요" />

            <div className="image-upload">
              <label htmlFor="file-upload" className="image-upload-label">
                이미지
              </label>
              <div className="image-upload-wrapper">
                <input
                  id="file-upload"
                  type="text"
                  placeholder="파일을 선택해 주세요"
                  className="image-upload-input"
                  value={selectedFile}
                  readOnly
                />
                <label htmlFor="file-upload" className="file-select-button">
                  파일 선택
                </label>
                <input
                  id="file-upload"
                  type="file"
                  className="file-upload-input"
                  onChange={handleFileSelect}
                />
              </div>
            </div>

            <label>본문</label>
            <textarea placeholder="본문 내용을 입력해 주세요" />
          </div>

          {/* 수직선을 추가하는 부분 */}
          <div className="vertical-line"></div>

          <div className="form-right">
            <label>태그</label>
            <input type="text" placeholder="태그 입력 후 Enter" />

            <label>장소</label>
            <input type="text" placeholder="장소를 입력해 주세요" />

            <label>추억의 순간</label>
            <input type="date" />

            <label>추억 공개 선택</label>
            <div className="toggle-container" onClick={handleToggle}>
              <div
                className={`toggle ${isPublic ? "public" : "private"}`}
              ></div>
            </div>

            <label>비밀번호</label>
            <input type="password" placeholder="비밀번호를 입력해 주세요" />
          </div>
        </div>
        <button className="submit-button">올리기</button>
      </div>
    </div>
  );
};

export default MemoryForm;
