import React, { useState } from "react";
import "./GroupDetail.css";
import groupImage from "../assets/GroupImage.png";
import flowerIcon from "../assets/size=16.png";
import GroupEditModal from "./GroupEditModal";
import GroupDeleteModal from "./GroupDeleteModal";
import noMemoriesImage from "../assets/icon=empty.svg"; 
import searchIcon from "../assets/icon=search.svg"; 

const GroupDetail = () => {
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [sortOption, setSortOption] = useState("Í≥µÍ∞êÏàú"); 

  const openEditModal = () => setIsEditModalOpen(true);
  const closeEditModal = () => setIsEditModalOpen(false);

  const openDeleteModal = () => setIsDeleteModalOpen(true);
  const closeDeleteModal = () => setIsDeleteModalOpen(false);

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  return (
    <div className="group-detail">
      <div className="group-header">
        <img src={groupImage} alt="Í∑∏Î£π Ïù¥ÎØ∏ÏßÄ" className="group-image" />
        <div className="group-info">
          <div className="group-meta">
            <span className="group-id">D+265</span>
            <span className="group-visibility"> | Í≥µÍ∞ú</span>
          </div>
          <div className="group-title-container">
            <h1 className="group-title">Îã¨Î¥âÏù¥ÎÑ§ Í∞ÄÏ°±</h1>
            <div className="group-submeta">
              <span className="group-memories">Ï∂îÏñµ 8</span>
              <span className="group-likes"> | Í∑∏Î£π Í≥µÍ∞ê 1.5K</span>
            </div>
          </div>
          <p className="group-description">
            ÏÑúÎ°ú Ìïú ÎßàÏùåÏúºÎ°ú ÏùëÏõêÌïòÍ≥† ÏïÑÎÅºÎäî Îã¨Î¥âÏù¥ÎÑ§ Í∞ÄÏ°±ÏûÖÎãàÎã§.
          </p>
          <div className="group-badges-section">
            <span className="badges-title">ÌöçÎìù Î∞∞ÏßÄ</span>
            <div className="group-badges">
              <span className="badge">üå∏ 7Ïùº Ïó∞ÏÜç Í≤åÏãúÍ∏Ä Îì±Î°ù</span>
              <span className="badge">üåü Í∑∏Î£π Í≥µÍ∞ê 1Îßå Í∞ú Ïù¥ÏÉÅ Î∞õÍ∏∞</span>
              <span className="badge">‚ù§Ô∏è Ï∂îÏñµ Í≥µÍ∞ê 1Îßå Í∞ú Ïù¥ÏÉÅ Î∞õÍ∏∞</span>
              <button className="send-like">
                <img src={flowerIcon} alt="ÍΩÉ ÏïÑÏù¥ÏΩò" className="flower-icon" />
                Í≥µÍ∞ê Î≥¥ÎÇ¥Í∏∞
              </button>
            </div>
          </div>
          <hr className="image-divider" />
        </div>
        <div className="group-actions">
          <button className="group-action-button" onClick={openEditModal}>Í∑∏Î£π Ï†ïÎ≥¥ ÏàòÏ†ïÌïòÍ∏∞</button>
          <button className="group-action-button" onClick={openDeleteModal}>Í∑∏Î£π ÏÇ≠Ï†úÌïòÍ∏∞</button>
        </div>
      </div>

      {/* Ï∂îÏñµ Î™©Î°ù ÏÑπÏÖò */}
      <div className="memory-section">
        <div className="memory-header">
          <h2 className="memory-title">Ï∂îÏñµ Î™©Î°ù</h2>
          <button className="upload-memory-button">Ï∂îÏñµ Ïò¨Î¶¨Í∏∞</button>
        </div>

        {/* Í≤ÄÏÉâ Î∞î */}
        <div className="memory-controls">
          <div className="memory-toggle">
            <button className="toggle-button active">Í≥µÍ∞ú</button>
            <button className="toggle-button">ÎπÑÍ≥µÍ∞ú</button>
          </div>
          <div className="memory-search-container">
            <input 
              className="memory-search"
              type="text"
              placeholder="ÌÉúÍ∑∏ ÌòπÏùÄ Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî"
            />
            <img src={searchIcon} alt="Í≤ÄÏÉâ ÏïÑÏù¥ÏΩò" className="search-icon" />
          </div>
          <select className="memory-sort" value={sortOption} onChange={handleSortChange}>
            <option value="Í≥µÍ∞êÏàú">Í≥µÍ∞êÏàú</option>
            <option value="ÏµúÏã†Ïàú">ÏµúÏã†Ïàú</option>
            <option value="Ïò§ÎûòÎêúÏàú">Ïò§ÎûòÎêúÏàú</option>
          </select>
        </div>

        {/* Ï∂îÏñµÏù¥ ÏóÜÏùÑ Îïå ÌëúÏãú */}
        <div className="no-memories">
          <img src={noMemoriesImage} alt="Ï∂îÏñµ ÏóÜÏùå" className="no-memories-image" />
          <p className="no-memories-text">Í≤åÏãúÎêú Ï∂îÏñµÏù¥ ÏóÜÏäµÎãàÎã§.<br />Ï≤´ Î≤àÏß∏ Ï∂îÏñµÏùÑ Ïò¨Î†§Î≥¥ÏÑ∏Ïöî!</p>
        </div>

        {/* ÌïòÎã®Ïóê 'Ï∂îÏñµ Ïò¨Î¶¨Í∏∞' Î≤ÑÌäº Ï∂îÍ∞Ä */}
        <div className="memory-footer">
          <button className="create-memory-button">Ï∂îÏñµ Ïò¨Î¶¨Í∏∞</button>
        </div>
      </div>

      {/* Í∑∏Î£π Ï†ïÎ≥¥ ÏàòÏ†ï Î™®Îã¨ */}
      <GroupEditModal isOpen={isEditModalOpen} onClose={closeEditModal} />

      {/* Í∑∏Î£π ÏÇ≠Ï†ú Î™®Îã¨ */}
      <GroupDeleteModal isOpen={isDeleteModalOpen} onClose={closeDeleteModal} />
    </div>
  );
};

export default GroupDetail;
