import React, { useState } from "react";
import "./GroupEditModal.css";
import closeIcon from "../assets/icon=x.png";

function GroupEditModal({ isOpen, onClose }) {
  const [groupName, setGroupName] = useState("달봉이네 가족");
  const [groupImage, setGroupImage] = useState("dalbong.jpg");
  const [groupDescription, setGroupDescription] = useState("");
  const [isPublic, setIsPublic] = useState(true);
  const [password, setPassword] = useState("");

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="modal-close-button" onClick={onClose}>
          <img src={closeIcon} alt="Close" />
        </button>
        <h2 className="modal-title">그룹 정보 수정</h2>

        <label className="form-label">그룹명</label>
        <input
          type="text"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
          className="form-input"
        />

        <label className="form-label">대표 이미지</label>
        <div className="form-input-file">
          <input
            type="text"
            value={groupImage}
            className="file-input-text"
            readOnly
          />
          <label className="file-button">
            파일 선택
            <input
              type="file"
              onChange={(e) => setGroupImage(e.target.files[0].name)}
              style={{ display: "none" }}
            />
          </label>
        </div>

        <label className="form-label">그룹 소개</label>
        <textarea
          value={groupDescription}
          onChange={(e) => setGroupDescription(e.target.value)}
          placeholder="그룹을 소개해 주세요"
          className="form-textarea"
        />

        <label className="form-label">그룹 공개 선택</label>
        <div className="form-public-toggle">
          <label className="toggle-label">공개</label>
          <label className="switch">
            <input
              type="checkbox"
              checked={isPublic}
              onChange={(e) => setIsPublic(e.target.checked)}
            />
            <span className="slider round"></span>
          </label>
        </div>

        <label className="form-label">수정 권한 인증</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="비밀번호를 입력해 주세요"
          className="form-input"
        />

        <button type="submit" className="form-button">
          수정하기
        </button>
      </div>
    </div>
  );
}

export default GroupEditModal;
