import React, { useState } from 'react';
import './MemoryForm.css';

const MemoryForm = () => {
  const [isPublic, setIsPublic] = useState(true);

  const handleToggle = () => {
    setIsPublic(!isPublic);
  };

  return (
    <div className="memory-form-container">
      <h1>추억 올리기</h1>
      <div className="memory-form">
        <div className="form-left">
          <label>닉네임</label>
          <input type="text" placeholder="닉네임을 입력해 주세요" />

          <label>제목</label>
          <input type="text" />

          <label>이미지</label>
          <input type="file" />

          <label>본문</label>
          <textarea placeholder="본문 내용을 입력해 주세요" />
        </div>

        <div className="form-right">
          <label>태그</label>
          <input type="text" placeholder="#낚시 #인천 #바다낚시" />

          <label>장소</label>
          <input type="text" placeholder="장소를 입력해 주세요" />

          <label>추억의 순간</label>
          <input type="date" />

          <label>추억 공개 선택</label>
          <div className="toggle-container" onClick={handleToggle}>
            <div className={`toggle ${isPublic ? 'public' : 'private'}`}></div>
          </div>

          <label>비밀번호</label>
          <input type="password" placeholder="비밀번호를 입력해 주세요" />
        </div>
      </div>
      <button className="submit-button">올리기</button>
    </div>
  );
};

export default MemoryForm;
