import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import GNB from "../components/GNB";
import "./HomePage.css";
import noGroupsImage from "../assets/icon=empty.svg";

const HomePage = () => {
  const [groups, setGroups] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [sortBy, setSortBy] = useState("like"); // 기본 정렬 기준: 최신순
  const [keyword, setKeyword] = useState("");
  const [isPublic, setIsPublic] = useState(true);
  const pageSize = 10; // 페이지당 아이템 수

  useEffect(() => {
    fetchGroups();
  }, [currentPage, sortBy, keyword, isPublic]);

  const fetchGroups = async () => {
    const response = await fetch(
      `http://localhost:3000/api/groups?page=${currentPage}&pageSize=${pageSize}&sortBy=${sortBy}&keyword=${keyword}&isPublic=${isPublic}`
    );
    const data = await response.json();
    setGroups(data.data);
    setCurrentPage(data.currentPage);
    setTotalPages(data.totalPages);
  };

  return (
    <div className="home-page">
      <GNB />
      <div className="group-section">
        <div className="group-toggle">
          <button
            className={`toggle-button ${isPublic ? "active" : ""}`}
            onClick={() => setIsPublic(true)}
          >
            공개
          </button>
          <button
            className={`toggle-button ${!isPublic ? "active" : ""}`}
            onClick={() => setIsPublic(false)}
          >
            비공개
          </button>
          <div className="group-search-container">
            <input
              type="text"
              placeholder="그룹명을 검색해 주세요"
              className="group-search"
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
            />
            <button className="search-icon" onClick={fetchGroups}>
              <i className="fas fa-search"></i>
            </button>
          </div>
          <select
            className="group-sort"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="latest">최신순</option>
            <option value="mostPosted">게시글 많은순</option>
            <option value="mostLiked">공감순</option>
            <option value="mostBadge">획득 배지순</option>
          </select>
        </div>
        <div className="group-content">
          {groups.length === 0 ? (
            <div className="no-group">
              <img src={noGroupsImage} alt="No Groups" />
              <p>등록된 그룹이 없습니다.</p>
              <p>가장 먼저 그룹을 만들어보세요!</p>
            </div>
          ) : (
            <div className="group-list">
              {groups.map((group) => (
                <div key={group.id} className="group-item">
                  <img
                    src={group.imageUrl}
                    alt={group.name}
                    className="group-item-image"
                  />
                  <div className="group-item-info">
                    <h3 className="group-item-title">{group.name}</h3>
                    <p className="group-item-description">
                      {group.introduction}
                    </p>
                    <div className="group-item-meta">
                      <span>
                        게시글: {group.postCount} | 공감: {group.likeCount} | 배지: {group.badgeCount}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
          <Link to="/GroupForm">
            <button className="create-group-button">그룹 만들기</button>
          </Link>
          <div className="pagination">
            <button
              disabled={currentPage === 1}
              onClick={() => setCurrentPage(currentPage - 1)}
            >
              이전
            </button>
            <span>
              {currentPage} / {totalPages}
            </span>
            <button
              disabled={currentPage === totalPages}
              onClick={() => setCurrentPage(currentPage + 1)}
            >
              다음
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
