import React, { useState } from "react";
import { Link } from "react-router-dom";  
import GNB from "../components/GNB";
import GroupEditModal from "./GroupEditModal";  // GroupEditModal 컴포넌트를 임포트
import "./HomePage.css";
import noGroupsImage from "../assets/icon=empty.svg";

const HomePage = () => {
  const [isPublic, setIsPublic] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false); // 모달 상태 관리

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <div className="home-page">
      <GNB />
      <div className="group-section">
        <div className="group-toggle">
          <button
            className={`toggle-button ${isPublic ? "active" : ""}`}
            onClick={() => setIsPublic(true)}
          >
            공개
          </button>
          <button
            className={`toggle-button ${!isPublic ? "active" : ""}`}
            onClick={() => setIsPublic(false)}
          >
            비공개
          </button>
          <div className="group-search-container">
            <input
              type="text"
              placeholder="그룹명을 검색해 주세요"
              className="group-search"
            />
            <button className="search-icon">
              <i className="fas fa-search"></i>
            </button>
          </div>
          <select className="group-sort">
            <option value="recent">공감순</option>
            <option value="popular">인기순</option>
          </select>
        </div>
        <div className="group-content">
          <div className="no-group">
            <img src={noGroupsImage} alt="No Groups" />
            <p>등록된 공개 그룹이 없습니다.</p>
            <p>가장 먼저 그룹을 만들어보세요!</p>
          </div>
          <Link to="/GroupForm">  
            <button className="create-group-button">그룹 만들기</button>
          </Link>
          {/* '그룹 정보 수정하기' 버튼 */}
          <button 
            className="edit-group-button"
            onClick={openModal} // 클릭 시 모달을 열기
          >
            그룹 정보 수정하기
          </button>
        </div>
      </div>
      {/* GroupEditModal 컴포넌트를 추가 */}
      <GroupEditModal isOpen={isModalOpen} onClose={closeModal} />
    </div>
  );
};

export default HomePage;
